mapper.update:
update member 
set	pw =#{pw}, email =#{email}, tel =#{tel}, roadaddress =#{roadaddress}, jibunaddress =#{jibunaddress},
detailaddress = #{detailaddress}, extraaddress = #{extraaddress}, gender = #{gender}
where membernum =1;

mapper.delete:
delete member
where membernum =1
and pw = #{pw};

mapper.update:
update address 
set	delroadaddress =#{delroadaddress}, deljibunaddress =#{deljibunaddress}, deldetailaddress =#{deldetailaddress}, 
delextraaddress =#{delextraaddress}, delpostcode =#{delpostcode}
where addressnum =#{addressnum};
