mapper.select:
select M.zno, M.membernum, M.itemnum, I.itemname, I.itempay, I.itemcontent, I.rstar, I.itemb, I.items, IM.imgnum, IM.imgname, IM.url, (select codestring from codetable where codename = "대분류" and codenum = I.itemb) as 'itembname',
(select codestring from codetable where codename = "소분류" and codenum = I.items) as 'itemsname'
from mylist M join item I join img IM where M.itemnum = I.itemnum and M.itemnum = IM.itemnum and membernum = #{membernum} order by zno desc LIMIT #{startIdx}, #{endIdx};

select count(*) as totalcount from dv_order O join address AD join cart C join item IT join member M join img IM join dv_order_item DO join iv_item IV
where O.buynum = DO.buynum and DO.cartno = C.cartno and C.itemnum = IT.itemnum and C.membernum = M.membernum and C.itemnum = IM.itemnum and O.addrnum = AD.addrnum and C.iv_itemnum = IV.iv_itemnum and M.membernum = #{membernum}
and O.buydate >= DATE_SUB(CURDATE(), INTERVAL 6 MONTH) and IT.itemname like concat("%",#{keyword},"%");
and YEAR(O.buydate) = 2023 and IT.itemname like concat("%",#{keyword},"%");
and YEAR(O.buydate) = 2022 and IT.itemname like concat("%",#{keyword},"%");
and YEAR(O.buydate) = 2021 and IT.itemname like concat("%",#{keyword},"%");
and YEAR(O.buydate) = 2020 and IT.itemname like concat("%",#{keyword},"%");

