mapper.select:
SELECT c.cartno, c.membernum, c.itemnum, c.itemcnt, c.cartstate, m.pointac, m.point, m.name, m.email, m.tel, m.roadaddr, m.jibunaddr, m.detailaddr, m.extraaddr, m.postcode, i.itemname, i.itempay, i.sale , i.itemnum, iv.itemsize, iv.itemcolor , iv.iv_itemnum, ad.addrnum , ad.membernum , ad.delroadaddr , ad.deljibunaddr , ad.deldetailaddr , ad.delextraaddr , ad.addrstate , ad.delpostcode, ( SELECT COUNT(*) FROM cart WHERE membernum = c.membernum ) AS cartcount, img.imgnum, img.url, img.imgname, img.itemnum, (SELECT codestring FROM codetable WHERE codename = '색깔' and codenum = iv.itemcolor) AS colorstring, (SELECT codestring FROM codetable WHERE codename = '사이즈' AND codenum = iv.itemsize) AS sizestring, rating.codestring AS ratingstring FROM cart c JOIN item i ON c.itemnum = i.itemnum JOIN member m ON c.membernum = m.membernum JOIN iv_item iv ON c.iv_itemnum = iv.iv_itemnum JOIN img ON i.itemnum = img.itemnum JOIN address ad ON c.membernum = ad.membernum JOIN ratinglist ON m.pointac BETWEEN ratinglist.lowpoint AND ratinglist.highpoint JOIN codetable rating ON ratinglist.rating = rating.codenum WHERE c.membernum IN
#{membernum}
AND c.cartstate = 1 AND rating.codename = '등급' and c.cartstate = 1 and ad.addrstate = 1 AND c.cartno IN
#{cartno}
GROUP BY c.cartno, c.membernum, c.itemnum, c.itemcnt, c.cartstate, i.itemname, i.itempay, i.sale, m.pointac, m.point, m.name, m.email, m.tel, m.roadaddr, m.jibunaddr, m.detailaddr, m.extraaddr, m.postcode, iv.itemsize, iv.itemcolor, iv.iv_itemnum, ad.addrnum , ad.membernum , ad.delroadaddr , ad.deljibunaddr , ad.deldetailaddr , ad.delextraaddr , ad.addrstate , ad.delpostcode, img.imgnum, img.url, img.imgname, img.itemnum, rating.codestring ORDER BY c.cartno;

mapper.insert:

INSERT INTO dv_order (buynum, addrnum, buydate, payment, paymethod, state, salevalue, pointvalue, deliveryday)
SELECT IFNULL(MAX(buynum), 0) + 1, #{addrnum}, NOW(), #{payment}, #{paymethod}, 3, #{salevalue}, #{pointvalue}, DATE_ADD(NOW(), INTERVAL 3 DAY)
FROM dv_order;

