UPDATE cart
SET cartstate = 2
WHERE cartno = #{cartno}

UPDATE iv_item
SET itemcnt = itemcnt -  

   #{itemcnt}
,
itembuycnt = itembuycnt + 

   #{itemcnt}


WHERE iv_itemnum IN

   #{iv_itemnum}

update.foreach:
UPDATE iv_item
SET itemcnt = itemcnt - #{item.itemcnt},
	   itembuycnt = itembuycnt + #{item.itemcnt} 

WHERE iv_itemnum = #{item.iv_itemnum}

