mapper.select:
select avg(rstar) AS rstar
from review
where itemnum = #{itemnum};

SELECT r.reviewno, r.rstar, r.rcontent, r.reviewlike, DATE_FORMAT(r.rdate, '%Y-%m-%d') AS rdate, (select id from member m where m.membernum=r.membernum) AS 'id' FROM review r WHERE del=1 and itemnum=#{itemnum}
AND ( reviewno LIKE CONCAT('%', #{keyword}, '%') OR rcontent LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY
rstar ASC rstar DESC reviewlike DESC reviewlike ASC rdate DESC rdate ASC CASE WHEN del=1 THEN 0 ELSE 1 END, reviewno DESC CASE WHEN del=1 THEN 0 ELSE 1 END, reviewno DESC
LIMIT #{startIdx}, #{pageSize}

SELECT membernum, itemnum, iv_itemnum
FROM cart
where membernum=#{membernum} and itemnum=#{itemnum} and iv_itemnum=#{iv_itemnum} and cartstate=1;

