mapper.select:
select r.reviewno,r.rstar,r.rcontent,r.membernum, i.itemname,im.url,im.imgname,
(select codestring from codetable where codename = "상품색상" and codenum = iv.itemcolor ) as 'color',
(select codestring from codetable where codename = "사이즈" and codenum = iv.itemsize ) as 'size' from review r join iv_item iv join dv_order_item doi join cart c join item i on r.itemnum = i.itemnum join img im on i.itemnum = im.itemnum join member m on r.membernum = m.membernum where r.buynum = doi.buynum and doi.cartno = c.cartno and r.itemnum = c.itemnum and c.iv_itemnum = iv.iv_itemnum and m.membernum = #{membernum} and reviewno = #{reviewno};

select count(*) from dv_order_item doi join cart c join dv_order dv
where doi.buynum = dv.buynum 
and doi.cartno = c.cartno
and dv.review = 1
and c.membernum = #{membernum}
;
